import axios from "axios";
import React ,{useState, useEffect} from "react";
import "./infobar.css"
export default function Infobar({currentChat}) {
    const [modification, setModification] = useState(false);
const [name, setName] = useState(currentChat.name)
const [newName, setNewName] = useState(null)


 //fonction qui envoie un message
 const handleModification = async (e) => {
  e.preventDefault();
  //BDD on ajoute le nouveau message en bdd et dans le tableau de messages
  const message = {
   newName
  };

  try {
    const res = await axios.post(
      `http://localhost:8080/api/conversation/name/${currentChat._id}`,
      message
    );
    setName(res.data);
    setNewName("");
    setModification(false)
  } catch (err) {
    console.log(err);
  }

};


  return (
    <div className="infobar">
        {modification?
        <>
        <input type="text" value={newName} onChange={(e) => setNewName(e.target.value)} />
        <button className="putButton" onClick={handleModification}>valid√©</button>
        </>
       :
       <>
       <h1>{name?name:null}</h1>
       <button className="putButton" onClick={()=>{setModification(true)}}>modifier</button>
       </>
        }


    </div>
  );
}
