import { useContext, useEffect, useState } from "react";
import { AuthContext } from "../../context/AuthContext";
import axios from "axios";
import "./messenger.css";
import Conversation from "../../components/Conversation/Conversation";
import Messagei from"../../components/Messagei/Messagei";
import {io} from "socket.io-client";
export default function Messenger() {
    const [newMessage, setNewMessage] = useState("");
  const [conversations, setConversations] = useState([]);
  const [currentChat, setCurrentChat] = useState(null);
  const [messages, setMessages] = useState([]);
  const [socket, setSocket] = useState(null);
  const { user } = useContext(AuthContext);

  useEffect(() => {
      setSocket(io("ws://localhost:8000"));
  },[])

  useEffect(() => {
    socket.on("welcome", message =>{

    })
},[])
  useEffect(() => {
    const getConversations = async () => {
      try {
        const res = await axios.get(
          "http://localhost:8000/api/conversation/" + user._id
        );
        setConversations(res.data);
        console.log("conversations", res);
      } catch (err) {
        console.log(err);
      }
    };
    getConversations();
  }, [user._id]);

  useEffect(() => {
    const getMessages = async () => {
      try {
        const res = await axios.get("http://localhost:8000/api/messages/" + currentChat._id);
        setMessages(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    getMessages();
  }, [currentChat]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const message = {
      sender: user._id,
      text: newMessage,
      conversationId: currentChat._id,
    };

    try {
        const res = await axios.post("http://localhost:8000/api/messages", message);
        setMessages([...messages, res.data]);
        setNewMessage("");
      } catch (err) {
        console.log(err);
      }
}

  return (
    <div>
      <h1>messenger</h1>
      <div>
        {conversations.map((conversation) => (
          <div onClick={()=> setCurrentChat(conversation)}>
            <Conversation
              key={conversation._id}
              conversation={conversation}
              currentUser={user}
            />
          </div>
        ))}
      </div>

      <div>
        <div className="chatBoxWrapper">
          {currentChat ? (
            <>
              <div className="chatBoxTop">
              {messages.map((m) => (
                      <Messagei message={m} own={m.sender === user._id} />
                  ))}
              </div>
              <div className="chatBoxBottom">
                <textarea
                  className="chatMessageInput"
                  placeholder="write something..."
                  onChange={(e) => setNewMessage(e.target.value)}
                  value={newMessage}
                ></textarea>
                <button onClick={handleSubmit}>
                  Send
                </button>
              </div>
            </>
          ) : (
            <span className="noConversationText">
              Open a conversation to start a chat.
            </span>
          )}
        </div>
      </div>
    </div>
  );
}
