import React from "react";

export default function Input({message, setMessage,}) {
  return (
    <div>
      <input
      type="text"
      placeholder="Type a Message"
        value={message}
        onChange={(event) => setMessage(event.target.value)}
        onKeyPress={(event) =>
          event.key === "Enter" ? sendMessage(event) : null
        }
      />
    </div>
  );
}
