import React, { useState, useEffect } from "react";
import "./createconv.css";
import axios from "axios";
export default function CreateConv() {
  const [users, setUsers] = useState([]);
  const [usersChecked, setUsersChecked] = useState([]);
//   useEffect(() => {
//     console.log("userschecked", usersChecked);
//   }, [usersChecked]);

  useEffect(() => {
    const getUsers = async () => {
      try {
        const res = await axios.get("http://localhost:8080/api/users/all");

        setUsers(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    getUsers();
  }, []);



  const handleChange = (e) => {
    const { name, checked } = e.target;
    if (name === "allSelect") {
      let tempUser = users.map((user) => {
        return { ...user, isChecked: checked };
      });
      setUsers(tempUser);
    } else {
      let tempUser = users.map((user) =>
        user.name === name ? { ...user, isChecked: checked } : user
      );
      setUsers(tempUser);
    }
  };

  return (
    <div className="createConvx">
      <div className="createConvBoxx">
        <h1>create conv</h1>

        <form className="form w-100">
          <h3>Select Users</h3>
          <div className="form-check">
            <input
              type="checkbox"
              className="form-check-input"
              name="allSelect"
              // checked={
              //   users.filter((user) => user?.isChecked !== true).length < 1
              // }
              checked={!users.some((user) => user?.isChecked !== true)}
              onChange={handleChange}
            />
            <label className="form-check-label ms-2">All Select</label>
          </div>
          {users.map((user, index) => (
            <div className="form-check" key={index}>
              <input
                type="checkbox"
                className="form-check-input"
                name={user.name}
                checked={user?.isChecked || false}
                onChange={handleChange}
              />
              <label className="form-check-label ms-2">{user.name}</label>
            </div>
          ))}
        </form>

        {/* <ul>
                    {users.map(user => (<div>
                        <li>{user.username}</li>
                        <input type="checkbox" name="username" value={user._id} onChange={(e)=>{setUsersChecked(e.value)}}/>
                        </div>))}
                </ul> */}
      </div>
    </div>
  );
}
