import axios from "axios";
import React ,{useState, useEffect} from "react";
import "./infobar.css"
export default function Infobar({currentChat}) {
    const [isEditing, setIsEditing] = useState(false);
const [name, setName] = useState(currentChat.name)
const [editedName, setEditedName] = useState("")

useEffect(() => {
  console.log(editedName);
  console.log(name);
},[editedName])

 //fonction qui envoie un message
 const handleModification = async (e) => {
  e.preventDefault();
  //BDD on ajoute le nouveau message en bdd et dans le tableau de messages

  const message = {
   "name":editedName
  };
  console.log(message);

  try {
    const res = await axios.put(
      `http://localhost:8080/api/conversation/name/${currentChat._id}`,
      message
    );
    setName(res.data);
    setEditedName("");
    setIsEditing(false);  
  } catch (err) {
    console.log(err);
  }

};


  return (
    <div className="infobar">




        {isEditing?

        <input type="text" value={isEditing?editedName:name} onChange={(e) => setEditedName(e.target.value)} />
        :
        <h1>{name?name:null}</h1>

        }



        {isEditing?

        <button className="putButton" onClick={handleModification}>valid√©</button>
        :
        <button className="putButton" onClick={()=>{setIsEditing(true)}}>modifier</button>
        }



    </div>
  );
}
