import React, { useState, useEffect } from "react";
import queryString from "query-string";
import io from "socket.io-client";
import Infobar from "../components/Infobar";
import Input from "../components/Input";
import Messages from "../components/Messages";
import Users from "../components/Users";
let socket;
export default function Chat() {
  //nom
  const [name, setName] = useState("");
  //room
  const [room, setRoom] = useState("");
  // le message envoyé
  const [message, setMessage] = useState("");
  //tout les messages
  const [messages, setMessages] = useState([]);
  //tout les messages
  const [users, setUsers] = useState("");
  //adress du server
  const ENDPOINT = "localhost:8000";

  useEffect(() => {
    //on recupere les parametre get de l'url
    const { name, room } = queryString.parse(window.location.search);
    //cree la connection
    socket = io(ENDPOINT);
    console.log(socket);
    // on recupere le name du gars et la room dans l'url et on la set
    setRoom(room);
    setName(name);
    // on envoie le nom du gars et la room
    socket.emit("join", { name, room }, (error) => {
      if (error) {
        alert(error);
      }
      return () => {
        socket.emit("disconnect");
        socket.off();
      };
    });
  }, [ENDPOINT, window.location.search]);

  useEffect(() => {
    //   si on recoit un message on le met dans le tableau messages
    socket.on("message", (message) => {
      setMessages((messages) => [...messages, message]);
    });
    socket.on("roomData", (data) => {
        setUsers(users);
        console.log("data",data);
      });

  },[]);

  const sendMessage = (event) => {
    event.preventDefault();
    //   on envoie le message et on reset l'input
    if (message){
      socket.emit("sendMessage", message, () => setMessage(""));
    }
  };
  console.log(message, messages);
  return (
    <div>
      <Infobar room={room} />
      <Messages messages={messages} name={name} />
      <Input
        message={message}
        setMessage={setMessage}
        sendMessage={sendMessage}
      />
      <Users users={users}/>
    </div>
  );
}
