import React, { useState, useEffect } from "react";
import "./createconv.css";
import axios from "axios";
export default function CreateConv( {currentUser}) {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    console.log("users", users)
    console.log("currentUser",currentUser);

  }, [users]);


  const checkedUser = users.find((user) => user.checked === true);


  useEffect(() => {
    const getUsers = async () => {
      try {
        const res = await axios.get("http://localhost:8080/api/users/all");
        setUsers(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    getUsers();
  }, []);


  const handleSubmit = async (e) => {
    e.preventDefault();

    const checkedUser = users.filter((user) => user.isChecked === true);
    let receivers = checkedUser.map(user => user._id)
    const convuser = {
        sender:currentUser._id,
        receivers
    }
      try {
        const res = await axios.post("http://localhost:8080/api/conversation/", convuser);
        console.log(res);
      } catch (err) {
        console.log(err);
      }


  };


  const handleChange = (e) => {
    const { name, checked } = e.target;     

    if (name === "allSelect") {
      let tempUser = users.map((user) => {
        return { ...user, isChecked: checked };
      });
      setUsers(tempUser);

    } else {
      let tempUser = users.map((user) =>
        user.username === name ? { ...user, isChecked: checked } : user
      );
      setUsers(tempUser);

    }
  };





  return (
    <div className="createConvx">
      <div className="createConvBoxx">

        <form className="form w-100">
          <h3>Select Users</h3>
          <div className="form-check">
            <input
              type="checkbox"
              className="form-check-input"
              name="allSelect"
              checked={!users.some((user) => user?.isChecked !== true)}
              onChange={handleChange}
            />
            <label className="form-check-label ms-2">All Select</label>
          </div>
          {users.map((user, index) => (
            <div className="form-check" key={index}>
              <input
                type="checkbox"
                className="form-check-input"
                name={user.username}
                checked={user?.isChecked || false}
                onChange={handleChange}
              />
              <label className="form-check-label ms-2">{user.username}</label>
            </div>
          ))}
          <input type="submit" onClick={handleSubmit} value="envoyaxx"/>
        </form>

      </div>
    </div>
  );
}
